package com.zstoryproject;import org.lwjgl.input.Keyboard;public class PlayerControll {	private String animationLastState = "idleDown";	public String[] animationState = {"idleDown","0","0"};	private boolean keyPressedFlag = false;	public String[] animationState() {		keyboardInit();		return animationState;	}		private void keyboardInit(){		while (Keyboard.next()) {	   		if (Keyboard.getEventKeyState()) {	   			if (Keyboard.getEventKey() == Keyboard.KEY_W && keyPressedFlag == false) {	   				animationState[0] = "walkUp";	   				animationLastState = "idleUp";	   				animationState[2] = "-2";					keyPressedFlag = true;				}				if (Keyboard.getEventKey() == Keyboard.KEY_S && keyPressedFlag == false) {					animationState[0] = "walkDown";					animationLastState = "idleDown";					animationState[2] = "2";					keyPressedFlag = true;				}				if (Keyboard.getEventKey() == Keyboard.KEY_A && keyPressedFlag == false) {					animationState[0] = "walkLeft";					animationLastState = "idleLeft";					animationState[1] = "-2";					keyPressedFlag = true;				}				if (Keyboard.getEventKey() == Keyboard.KEY_D && keyPressedFlag == false) {					animationState[0] = "walkRight";					animationLastState = "idleRight";					animationState[1] = "2";					keyPressedFlag = true;				}					    	} else {	    		animationState[0] = animationLastState;	    		animationState[1] = "0";	    		animationState[2] = "0";				keyPressedFlag = false;	    	}	  	   	}	}	}